import React from "react";
import Aurora from "./Aurora";
import "./Aurora.css";
import MainImg from "./assets/Main.png";

function App() {
  return (
    <>
      {/* Aurora background */}
      <Aurora colorStops={["#5227FF", "#7CFF67", "#5227FF"]} blend={0.5} amplitude={1.0} speed={0.5} />
      {/* Login Button Top Right */}
      <button
        className="glass-btn"
        style={{
          position: "absolute",
          top: "2rem",
          right: "2rem",
          zIndex: 20,
          marginBottom: 0,
          padding: "12px 28px",
          fontSize: "1rem"
        }}
        onClick={() => {
          // Add your login action here
          console.log("Login clicked!");
        }}
      >
        Login
      </button>
      {/* Main content column */}
      <div
        style={{
          position: "relative",
          minHeight: "100vh",
          width: "100vw",
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          justifyContent: "center",
          zIndex: 2,
        }}
      >
        {/* Hero Section */}
        <div style={{
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          justifyContent: "center",
          minHeight: "60vh",
          width: "100vw",
          pointerEvents: "auto",
          marginTop: "5rem",
        }}>
          <div style={{ textAlign: "center", marginBottom: "3rem" }}>
            <div
              style={{
                fontFamily: 'Figtree, Montserrat, Inter, Arial, sans-serif',
                fontWeight: 700,
                fontSize: "clamp(2.5rem, 7vw, 5rem)",
                color: "#fff",
                marginBottom: "0.2em",
                lineHeight: 1.1,
                userSelect: "none",
                textShadow: "0 2px 16px rgba(0,0,0,0.5)",
              }}
            >
              Turn Product Into
            </div>
            <div
              className="dancing-script-hero"
              style={{
                fontSize: "clamp(2rem, 5vw, 3.5rem)",
                color: "#fff",
                display: "inline-block",
                userSelect: "none",
                textShadow: "0 2px 16px rgba(0,0,0,0.5)",
              }}
            >
              Stunning Visual
              <span
                style={{
                  fontFamily: 'Figtree, Montserrat, Inter, Arial, sans-serif',
                  fontStyle: 'normal',
                  fontWeight: 700,
                  fontSize: "clamp(2rem, 5vw, 3.5rem)",
                  marginLeft: "0.3em",
                }}
              >
                Ads
              </span>
            </div>
          </div>
          <button
            className="glass-btn"
            onClick={() => {
              // Add your button action here
              console.log("Button clicked!");
            }}
          >
            Get Started
          </button>
        </div>
        {/* Image Section BELOW hero/button */}
        <div style={{
          width: "100vw",
          display: "flex",
          justifyContent: "center",
          alignItems: "flex-start",
          marginTop: "2rem",
          marginBottom: "2rem",
        }}>
          <img
            src={MainImg}
            alt="Main visual"
            style={{
              width: "min(95vw, 1000px)",
              height: "auto",
              borderRadius: "2rem",
              boxShadow: "0 4px 32px rgba(0,0,0,0.25)",
              objectFit: "contain",
              background: "#222",
              display: "block"
            }}
          />
        </div>
      </div>
    </>
  );
}

export default App;
