import React, { useState } from "react";
import "./Aurora.css";
import MainImg from "./assets/Main.png";
import LogoImg from "./assets/logo.png";
import { RainbowButtonDemo } from "./components/magicui/rainbow-button";
import Noise from "./components/Noise";
import BlurText from "./components/BlurText";
import ScrollReveal from "./components/ScrollReveal";
import { AiFillHome } from "react-icons/ai";
import { MdAutoAwesome } from "react-icons/md";
import { FaRegMoneyBillAlt } from "react-icons/fa";
import { IoMailOutline } from "react-icons/io5";
import { FiLogIn } from "react-icons/fi";

function Navbar() {
  const [active, setActive] = useState("home");
  // Define shared styles for button and pill
  const navItemStyle = {
    fontWeight: 400,
    fontSize: "1.05rem",
    padding: "0.6em 1.6em",
    height: "2.6em",
    display: "flex",
    alignItems: "center",
    fontFamily: "Figtree, Arial, Helvetica, sans-serif",
    boxSizing: "border-box",
  };

  return (
    <nav
      style={{
        position: "static",
        width: "100vw",
        background: "transparent",
        pointerEvents: "none",
        overflow: "visible",
      }}
    >
      <div
        style={{
          width: "100vw",
          maxWidth: "100vw",
          display: "flex",
          alignItems: "center",
          justifyContent: "space-between",
          position: "relative",
          padding: "0 2.5vw",
          pointerEvents: "auto",
          boxSizing: "border-box",
          overflow: "visible",
          marginTop: "2.5rem",
        }}
      >
        {/* Logo on far left */}
        <img
          src={LogoImg}
          alt="MagicAds logo"
          style={{
            height: "50px",
            width: "auto",
            display: "block",
            marginRight: "0.5em",
            flex: "0 0 auto",
            userSelect: "none",
          }}
        />
        {/* Center pill-shaped container (now with links) */}
        <div
          style={{
            ...navItemStyle,
            background: "rgba(255,255,255,0.07)",
            boxShadow: "0 4px 24px 0 rgba(0,0,0,0.08)",
            borderRadius: "2.5em",
            minWidth: 120,
            maxWidth: 520,
            width: "100%",
            margin: "0 auto",
            justifyContent: "center",
            pointerEvents: "auto",
            flex: "1 1 0%",
            overflow: "hidden",
            border: "1px solid rgba(255,255,255,0.13)",
            backdropFilter: "blur(16px)",
            WebkitBackdropFilter: "blur(16px)",
            opacity: 0.92,
            gap: "0.4em",
          }}
        >
          <a href="#" className={`nav-link${active === "home" ? " active" : ""}`} onClick={() => setActive("home")}><AiFillHome size={20}/>Home</a>
          <a href="#features" className={`nav-link${active === "features" ? " active" : ""}`} onClick={() => setActive("features") }><MdAutoAwesome size={20}/>Features</a>
          <a href="#pricing" className={`nav-link${active === "pricing" ? " active" : ""}`} onClick={() => setActive("pricing") }><FaRegMoneyBillAlt size={18}/>Pricing</a>
          <a href="#contact" className={`nav-link${active === "contact" ? " active" : ""}`} onClick={() => setActive("contact") }><IoMailOutline size={19}/>Contact</a>
        </div>
        {/* Login button on far right */}
        <button
          style={{
            ...navItemStyle,
            borderRadius: "2.5em",
            background: "rgba(255,255,255,0.07)",
            boxShadow: "0 4px 24px 0 rgba(0,0,0,0.08)",
            border: "1px solid rgba(255,255,255,0.13)",
            backdropFilter: "blur(16px)",
            WebkitBackdropFilter: "blur(16px)",
            color: "#fff",
            fontWeight: 400,
            fontSize: "1.08rem",
            display: "flex",
            alignItems: "center",
            gap: "0.6em",
            pointerEvents: "auto",
            flex: "0 0 auto",
            minWidth: 0,
            margin: 0,
            opacity: 0.92,
            cursor: "pointer",
            transition: "background 0.2s, border 0.2s, box-shadow 0.2s",
          }}
          onClick={() => {
            // Add your login action here
            console.log("Login clicked!");
          }}
        >
          <FiLogIn size={20} />
          Login
        </button>
      </div>
    </nav>
  );
}

function App() {
  return (
    <>
      <div className="landing-bg">
        <div className="grid-bg" />
        <div className="bg-semicircle" />
        <Noise patternSize={250} patternScaleX={1} patternScaleY={1} patternRefreshInterval={2} patternAlpha={15} />
        <Navbar />
        {/* Main content column */}
        <div
          style={{
            position: "relative",
            minHeight: "100vh",
            width: "100vw",
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
            justifyContent: "center",
            zIndex: 2,
            overflowY: "auto",
          }}
        >
          {/* Hero Section */}
          <div style={{
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
            justifyContent: "center",
            minHeight: "60vh",
            width: "100vw",
            pointerEvents: "auto",
            marginTop: "2.5rem",
          }}>
            <div className="hero-headline" style={{ textAlign: "center" }}>
              <div className="hero-figtree-bold" style={{ fontSize: "clamp(4rem, 12vw, 4.5rem)", color: "#fff", marginBottom: 0, lineHeight: 1, padding: 0, display: 'block' }}>
                <BlurText
                  text="Make Every Product"
                  delay={150}
                  animateBy="words"
                  direction="top"
                  className="inline"
                  display="block"
                />
              </div>
              <div style={{ fontSize: "clamp(3rem, 8vw, 5rem)", color: "#3ecbff", marginTop: 0, marginBottom: '0.4em', fontFamily: "'Dancing Script', cursive", lineHeight: 1, padding: 0, display: 'block' }}>
                <BlurText
                  text="Shine"
                  delay={450}
                  animateBy="words"
                  direction="top"
                  spanClassName="shine-gradient-animated"
                  display="block"
                />
              </div>
              <div style={{ color: '#cbd5e1', fontSize: '1.1rem', fontWeight: 400, textAlign: 'center', letterSpacing: 0 }}>
                Create stunning ads from your product with AI, no design needed.
              </div>
            </div>
            <RainbowButtonDemo />
            <div className="hero-muted-small">No credit card required</div>
          </div>
          {/* Image Section BELOW hero/button */}
          <div style={{
            width: "100vw",
            display: "flex",
            justifyContent: "center",
            alignItems: "flex-start",
            marginTop: "2rem",
            marginBottom: "2rem",
          }}>
            <div className="glass-card">
              <img
                src={MainImg}
                alt="Main visual"
                style={{
                  width: "min(95vw, 1000px)",
                  height: "auto",
                  borderRadius: "2rem",
                  boxShadow: "0 4px 32px rgba(0,0,0,0.25)",
                  objectFit: "contain",
                  background: "#222",
                  display: "block"
                }}
              />
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default App;
