import React from "react";
import Aurora from "./Aurora";
import "./Aurora.css";
import MainImg from "./assets/Main.png";
import { RainbowButton } from "./components/magicui/rainbow-button";

function App() {
  return (
    <>
      {/* Aurora background */}
      <Aurora colorStops={["#5227FF", "#7CFF67", "#5227FF"]} blend={0.5} amplitude={1.0} speed={0.5} />
      <div className="landing-bg">
        {/* Login Button Top Right */}
        <button
          className="glass-btn"
          style={{
            position: "absolute",
            top: "2rem",
            right: "2rem",
            zIndex: 20,
            marginBottom: 0,
            padding: "12px 28px",
            fontSize: "1rem"
          }}
          onClick={() => {
            // Add your login action here
            console.log("Login clicked!");
          }}
        >
          Login
        </button>
        {/* Main content column */}
        <div
          style={{
            position: "relative",
            minHeight: "100vh",
            width: "100vw",
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
            justifyContent: "center",
            zIndex: 2,
          }}
        >
          {/* Hero Section */}
          <div style={{
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
            justifyContent: "center",
            minHeight: "60vh",
            width: "100vw",
            pointerEvents: "auto",
            marginTop: "5rem",
          }}>
            <div className="hero-headline">
              <div className="hero-figtree-bold" style={{ fontSize: "clamp(2.2rem, 6vw, 3.5rem)", color: "#fff", lineHeight: 1.08, userSelect: "none", textShadow: "0 2px 16px rgba(0,0,0,0.5)" }}>
                Showcase Your Product
              </div>
              <div style={{ display: "flex", justifyContent: "center", alignItems: "flex-end", flexWrap: "wrap", gap: "0.5rem", marginTop: "0.5rem" }}>
                <span className="hero-script" style={{ fontSize: "clamp(1.5rem, 5vw, 2.5rem)" }}>Like Never Before</span>
              </div>
            </div>
            <div className="hero-muted">Premium. AI powered. 2 minutes.</div>
            <RainbowButton style={{ fontSize: "1.35rem", borderRadius: "2em", padding: "1.1em 2.5em", background: "#111" }}>
              Create your first ad now - 5 credits free
            </RainbowButton>
            <div className="hero-muted-small">No credit card required</div>
          </div>
          {/* Image Section BELOW hero/button */}
          <div style={{
            width: "100vw",
            display: "flex",
            justifyContent: "center",
            alignItems: "flex-start",
            marginTop: "2rem",
            marginBottom: "2rem",
          }}>
            <img
              src={MainImg}
              alt="Main visual"
              style={{
                width: "min(95vw, 1000px)",
                height: "auto",
                borderRadius: "2rem",
                boxShadow: "0 4px 32px rgba(0,0,0,0.25)",
                objectFit: "contain",
                background: "#222",
                display: "block"
              }}
            />
          </div>
        </div>
      </div>
    </>
  );
}

export default App;
