import React from "react";
import Aurora from "./Aurora";
import "./Aurora.css";
import MainImg from "./assets/Main.png";

function App() {
  return (
    <>
      <Aurora colorStops={["#5227FF", "#7CFF67", "#5227FF"]} blend={0.5} amplitude={1.0} speed={0.5} />
      {/* Overlay for hero and button */}
      <div
        style={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100vw",
          height: "100vh",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          flexDirection: "column",
          zIndex: 10,
          pointerEvents: "none",
        }}
      >
        <div style={{ textAlign: "center", pointerEvents: "auto", marginBottom: "3rem" }}>
          <div
            style={{
              fontFamily: 'Figtree, Montserrat, Inter, Arial, sans-serif',
              fontWeight: 700,
              fontSize: "clamp(2.5rem, 7vw, 5rem)",
              color: "#fff",
              marginBottom: "0.2em",
              lineHeight: 1.1,
              userSelect: "none",
              textShadow: "0 2px 16px rgba(0,0,0,0.5)",
            }}
          >
            Turn Product Into
          </div>
          <div
            style={{
              fontFamily: 'Georgia, Times New Roman, Times, serif',
              fontStyle: 'italic',
              fontWeight: 400,
              fontSize: "clamp(2rem, 5vw, 3.5rem)",
              color: "#fff",
              display: "inline-block",
              userSelect: "none",
              textShadow: "0 2px 16px rgba(0,0,0,0.5)",
            }}
          >
            Stunning Visual
            <span
              style={{
                fontFamily: 'Figtree, Montserrat, Inter, Arial, sans-serif',
                fontStyle: 'normal',
                fontWeight: 700,
                fontSize: "clamp(2rem, 5vw, 3.5rem)",
                marginLeft: "0.3em",
              }}
            >
              Ads
            </span>
          </div>
        </div>
        <div style={{ textAlign: "center", pointerEvents: "auto", marginBottom: "2.5rem" }}>
          <button
            style={{
              background: "linear-gradient(135deg, #5227FF 0%, #7CFF67 100%)",
              border: "none",
              borderRadius: "50px",
              padding: "16px 32px",
              fontSize: "clamp(1rem, 2.5vw, 1.25rem)",
              fontWeight: 600,
              color: "#fff",
              cursor: "pointer",
              fontFamily: 'Figtree, Montserrat, Inter, Arial, sans-serif',
              boxShadow: "0 8px 32px rgba(82, 39, 255, 0.3)",
              transition: "all 0.3s ease",
              userSelect: "none",
              pointerEvents: "auto",
            }}
            onMouseEnter={(e) => {
              e.target.style.transform = "translateY(-2px)";
              e.target.style.boxShadow = "0 12px 40px rgba(82, 39, 255, 0.4)";
            }}
            onMouseLeave={(e) => {
              e.target.style.transform = "translateY(0)";
              e.target.style.boxShadow = "0 8px 32px rgba(82, 39, 255, 0.3)";
            }}
            onClick={() => {
              // Add your button action here
              console.log("Button clicked!");
            }}
          >
            Get Started
          </button>
        </div>
      </div>
      {/* Big image below overlay, centered */}
      <div style={{
        width: "100vw",
        display: "flex",
        justifyContent: "center",
        alignItems: "flex-end",
        position: "absolute",
        left: 0,
        bottom: 0,
        zIndex: 1,
        pointerEvents: "none"
      }}>
        <img
          src={MainImg}
          alt="Main visual"
          style={{
            width: "min(95vw, 900px)",
            height: "auto",
            borderRadius: "2rem",
            boxShadow: "0 4px 32px rgba(0,0,0,0.25)",
            objectFit: "cover",
            background: "#222",
            display: "block",
            maxHeight: "70vh"
          }}
        />
      </div>
    </>
  );
}

export default App;
