import React from "react";
import "./Aurora.css";
import MainImg from "./assets/Main.png";
import { RainbowButtonDemo } from "./components/magicui/rainbow-button";
import Noise from "./components/Noise";

function App() {
  return (
    <>
      <div className="landing-bg">
        <div className="bg-semicircle" />
        <Noise patternSize={250} patternScaleX={1} patternScaleY={1} patternRefreshInterval={2} patternAlpha={15} />
        {/* Login Button Top Right */}
        <button
          className="glass-btn"
          style={{
            position: "absolute",
            top: "2rem",
            right: "2rem",
            zIndex: 20,
            marginBottom: 0,
            padding: "12px 28px",
            fontSize: "1rem"
          }}
          onClick={() => {
            // Add your login action here
            console.log("Login clicked!");
          }}
        >
          Login
        </button>
        {/* Main content column */}
        <div
          style={{
            position: "relative",
            minHeight: "100vh",
            width: "100vw",
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
            justifyContent: "center",
            zIndex: 2,
          }}
        >
          {/* Hero Section */}
          <div style={{
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
            justifyContent: "center",
            minHeight: "60vh",
            width: "100vw",
            pointerEvents: "auto",
            marginTop: "5rem",
          }}>
            <div className="hero-headline">
              <div className="hero-script" style={{ fontSize: "clamp(4rem, 12vw, 5rem)", color: "#fff", textAlign: "center", marginBottom: "0.5rem" }}>
                Make Every Product Shine
              </div>
              <div style={{ color: '#cbd5e1', fontSize: '1.1rem', fontWeight: 400, textAlign: 'center', letterSpacing: 0 }}>
                Create stunning ads from your product with AI, no design needed.
              </div>
            </div>
            <RainbowButtonDemo />
            <div className="hero-muted-small">No credit card required</div>
          </div>
          {/* Image Section BELOW hero/button */}
          <div style={{
            width: "100vw",
            display: "flex",
            justifyContent: "center",
            alignItems: "flex-start",
            marginTop: "2rem",
            marginBottom: "2rem",
          }}>
            <img
              src={MainImg}
              alt="Main visual"
              style={{
                width: "min(95vw, 1000px)",
                height: "auto",
                borderRadius: "2rem",
                boxShadow: "0 4px 32px rgba(0,0,0,0.25)",
                objectFit: "contain",
                background: "#222",
                display: "block"
              }}
            />
          </div>
        </div>
      </div>
    </>
  );
}

export default App;
