import React from "react";
import Aurora from "./Aurora";
import "./Aurora.css";

function App() {
  return (
    <>
      <Aurora colorStops={["#5227FF", "#7CFF67", "#5227FF"]} blend={0.5} amplitude={1.0} speed={0.5} />
      <div
        style={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100vw",
          height: "100vh",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          zIndex: 2,
          pointerEvents: "none",
        }}
      >
        <h1
          style={{
            fontFamily: 'Montserrat, Inter, Arial, sans-serif',
            fontWeight: 800,
            fontSize: "clamp(2rem, 4vw, 3rem)",
            color: "#fff",
            margin: 0,
            textAlign: "center",
            lineHeight: 1.15,
            userSelect: "none",
          }}
        >
          Turn Any Product Into a<br />Stunning Visual Ad
        </h1>
      </div>
    </>
  );
}

export default App;
