import React from "react";
import "./Aurora.css";
import MainImg from "./assets/Main.png";
import { RainbowButtonDemo } from "./components/magicui/rainbow-button";
import Noise from "./components/Noise";

function Navbar() {
  return (
    <nav style={{
      position: "fixed",
      top: "2.5rem",
      left: "50%",
      transform: "translateX(-50%)",
      zIndex: 30,
      background: "rgba(255,255,255,0.92)",
      boxShadow: "0 4px 24px 0 rgba(0,0,0,0.08)",
      borderRadius: "2.5em",
      padding: "0.5em 2.2em 0.5em 1.5em",
      minWidth: 320,
      maxWidth: 520,
      width: "90vw",
      display: "flex",
      alignItems: "center",
      justifyContent: "space-between",
      gap: "1.5em"
    }}>
      {/* Logo placeholder */}
      <div style={{ fontWeight: 700, fontSize: "1.2rem", color: "#222", opacity: 0.5 }}>
        Logo
      </div>
      <button
        className="glass-btn"
        style={{
          borderRadius: "2em",
          fontWeight: 400,
          fontSize: "1.05rem",
          padding: "0.6em 1.6em",
          background: "rgba(255,255,255,0.7)",
          color: "#222",
          boxShadow: "0 2px 8px rgba(0,0,0,0.06)",
          border: "1px solid rgba(0,0,0,0.07)",
          margin: 0
        }}
        onClick={() => {
          // Add your login action here
          console.log("Login clicked!");
        }}
      >
        Login
      </button>
    </nav>
  );
}

function App() {
  return (
    <>
      <div className="landing-bg">
        <div className="bg-semicircle" />
        <Noise patternSize={250} patternScaleX={1} patternScaleY={1} patternRefreshInterval={2} patternAlpha={15} />
        <Navbar />
        {/* Main content column */}
        <div
          style={{
            position: "relative",
            minHeight: "100vh",
            width: "100vw",
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
            justifyContent: "center",
            zIndex: 2,
          }}
        >
          {/* Hero Section */}
          <div style={{
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
            justifyContent: "center",
            minHeight: "60vh",
            width: "100vw",
            pointerEvents: "auto",
            marginTop: "5rem",
          }}>
            <div className="hero-headline">
              <div className="hero-script" style={{ fontSize: "clamp(4rem, 12vw, 4.5rem)", color: "#fff", textAlign: "center", marginBottom: "0.5rem" }}>
                Make Every Product Shine
              </div>
              <div style={{ color: '#cbd5e1', fontSize: '1.1rem', fontWeight: 400, textAlign: 'center', letterSpacing: 0 }}>
                Create stunning ads from your product with AI, no design needed.
              </div>
            </div>
            <RainbowButtonDemo />
            <div className="hero-muted-small">No credit card required</div>
          </div>
          {/* Image Section BELOW hero/button */}
          <div style={{
            width: "100vw",
            display: "flex",
            justifyContent: "center",
            alignItems: "flex-start",
            marginTop: "2rem",
            marginBottom: "2rem",
          }}>
            <img
              src={MainImg}
              alt="Main visual"
              style={{
                width: "min(95vw, 1000px)",
                height: "auto",
                borderRadius: "2rem",
                boxShadow: "0 4px 32px rgba(0,0,0,0.25)",
                objectFit: "contain",
                background: "#222",
                display: "block"
              }}
            />
          </div>
        </div>
      </div>
    </>
  );
}

export default App;
