import * as React from "react";

export function RainbowButton({ children, className = "", ...props }) {
  return (
    <button
      className={
        `relative inline-flex h-12 items-center justify-center rounded-md border border-transparent bg-gradient-to-r from-pink-500 via-yellow-500 to-green-500 px-6 py-3 font-medium text-white shadow-lg transition-all duration-300 ease-out hover:scale-105 hover:shadow-xl focus:outline-none focus:ring-2 focus:ring-pink-400 focus:ring-offset-2 ${className}`
      }
      {...props}
    >
      <span className="relative z-10 font-semibold" style={{ background: "linear-gradient(90deg, #fff 0%, #fff 100%)", WebkitBackgroundClip: "text", WebkitTextFillColor: "transparent" }}>
        {children}
      </span>
      <span className="absolute inset-0 rounded-md bg-gradient-to-r from-pink-500 via-yellow-500 to-green-500 opacity-60 blur-md" />
    </button>
  );
}
