import React from "react";
import "./Aurora.css";
import MainImg from "./assets/Main.png";
import LogoImg from "./assets/logo.png";
import Noise from "./components/Noise";

function UiverseButton() {
  return (
    <div className="uiverse-btn-wrapper">
      <div className="uiverse-btn-bg"></div>
      <button className="uiverse-btn" type="button">
        Get Started For Free
        <svg aria-hidden="true" viewBox="0 0 10 10">
          <path d="M0 5h7" className="arrow-line" />
          <path d="M1 1l4 4-4 4" className="arrow-head" />
        </svg>
      </button>
    </div>
  );
}

function Navbar() {
  return (
    <nav>
      <div className="nav-inner">
        {/* Logo on far left */}
        <img src={LogoImg} alt="MagicAds logo" className="logo-img" />
        {/* Center pill-shaped nav links */}
        <div className="nav-pill">
          <a href="#" className="nav-link active">Home</a>
          <a href="#" className="nav-link">Resources</a>
          <a href="#" className="nav-link">Features</a>
          <a href="#" className="nav-link">FAQ</a>
          <a href="#" className="nav-link">Pricing</a>
        </div>
        {/* Login/Signup buttons on far right */}
        <div style={{ display: 'flex', gap: '0.7rem' }}>
          <button className="nav-btn">Log in</button>
          <button className="nav-btn" style={{ background: '#fff', color: '#222', border: '1.5px solid #fff' }}>Sign up</button>
        </div>
      </div>
    </nav>
  );
}

function App() {
  return (
    <div className="landing-bg">
      <div className="grid-bg" />
      <div className="bg-spotlight" />
      <Noise patternSize={250} patternScaleX={1} patternScaleY={1} patternRefreshInterval={2} patternAlpha={15} />
      <Navbar />
      <div className="main-content-card">
        {/* Hero Section */}
        <div className="hero-headline">
          <div className="hero-title">
            Make Every Product <span className="accent">Shine</span>
          </div>
          <div className="hero-sub">
            Create stunning ads from your product with AI, no design needed.
          </div>
          <div className="hero-btn-row">
            <UiverseButton />
            <button className="secondary-btn">Try Demo</button>
          </div>
          <div className="hero-muted-small">No credit card required</div>
        </div>
        {/* Showcase Card */}
        <div className="showcase-card">
          <img src={MainImg} alt="Main visual" className="showcase-img" />
        </div>
      </div>
    </div>
  );
}

export default App;
