import React from "react";
import Aurora from "./Aurora";
import "./Aurora.css";

function App() {
  return (
    <>
      <Aurora colorStops={["#5227FF", "#7CFF67", "#5227FF"]} blend={0.5} amplitude={1.0} speed={0.5} />
      <div
        style={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100vw",
          height: "100vh",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          flexDirection: "column",
          zIndex: 2,
          pointerEvents: "none",
        }}
      >
        <div style={{ textAlign: "center" }}>
          <div
            style={{
              fontFamily: 'Figtree, Montserrat, Inter, Arial, sans-serif',
              fontWeight: 700,
              fontSize: "clamp(2.2rem, 5vw, 3.5rem)",
              color: "#fff",
              lineHeight: 1.15,
              userSelect: "none",
            }}
          >
            Turn Any Product Into a<br />Stunning Visual Ads
          </div>
        </div>
      </div>
    </>
  );
}

export default App;
